import React, { useEffect, useState } from "react";
import axios from "axios";

const ReminderViewer = () => {
  const [reminders, setReminders] = useState([]);
  const [error, setError] = useState("");

  useEffect(() => {
    const fetchReminders = async () => {
      try {
        const res = await axios.get("http://localhost:8000/get/reminder");
        setReminders(res.data);
      } catch (err) {
        console.error(err);
        setError("Failed to load reminders.");
      }
    };

    fetchReminders();
  }, []);

  return (
    <div className="max-w-3xl mx-auto p-6 mt-10 bg-white dark:bg-gray-900 rounded-xl shadow-lg border border-blue-100 dark:border-gray-700">
      <h2 className="text-2xl font-bold text-blue-700 dark:text-blue-300 mb-6 text-center">
        Your Medication Reminders
      </h2>

      {error && <p className="text-red-600 text-center">{error}</p>}

      <ul className="space-y-4">
        {reminders.length > 0 ? (
          reminders.map((reminder, index) => (
            <li
              key={index}
              className="p-4 border border-gray-300 dark:border-gray-700 rounded-lg bg-gray-50 dark:bg-gray-800 shadow-sm"
            >
              <p className="text-lg font-medium text-gray-800 dark:text-white">
                üíä {reminder.medicine_name}
              </p>
              <p className="text-gray-600 dark:text-gray-300">‚è∞ Time: {reminder.time}</p>
            </li>
          ))
        ) : (
          <p className="text-gray-600 dark:text-gray-400 text-center">
            No reminders found.
          </p>
        )}
      </ul>
    </div>
  );
};

export default ReminderViewer;
