import { useEffect, useState } from "react";
import axios from "axios";

const FAQManager = () => {
  const [faqs, setFaqs] = useState([]);
  const [question, setQuestion] = useState("");
  const [answer, setAnswer] = useState("");

  // Fetch all FAQs
  const fetchFaqs = async () => {
    try {
        const res = await axios.get("http://localhost:8000/faq/all");
        setFaqs(res.data.faqs);
      } catch (err) {
        setError("Failed to load FAQs.");
        console.error(err);
      }
  }

  useEffect(() => {
    fetchFaqs();
  }, []);

  const handleAddFAQ = async (e) => {
    e.preventDefault();
    try {
      await axios.post("http://localhost:8000/faq/add", { question, answer });
      setQuestion("");
      setAnswer("");
      fetchFaqs();
    } catch (error) {
      alert("❌ Failed to add FAQ.");
    }
  };

  return (
    <div className="max-w-3xl mx-auto p-6">
      <h2 className="text-3xl font-bold text-blue-800 mb-6">FAQ Manager</h2>

      {/* Add FAQ */}
      <form onSubmit={handleAddFAQ} className="space-y-4 mb-8">
        <input
          type="text"
          value={question}
          onChange={(e) => setQuestion(e.target.value)}
          placeholder="Enter question"
          className="w-full p-3 border rounded-lg"
          required
        />
        <textarea
          value={answer}
          onChange={(e) => setAnswer(e.target.value)}
          placeholder="Enter answer"
          className="w-full p-3 border rounded-lg"
          required
        />
        <button className="px-6 py-3 bg-blue-600 text-white rounded-lg hover:bg-blue-700">
          Add FAQ
        </button>
      </form>

      <div className="space-y-4">
        {faqs.map((faq, index) => (
          <div key={index} className="p-4 border rounded-lg shadow-md bg-white">
            <h3 className="font-semibold text-lg text-blue-600">{faq.question}</h3>
            <p className="text-gray-800 mt-2">{faq.answer}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default FAQManager;
