// src/components/FAQViewer.jsx

import React, { useEffect, useState } from "react";
import axios from "axios";

const FAQViewer = () => {
  const [faqs, setFaqs] = useState([]);
  const [error, setError] = useState("");

  useEffect(() => {
    const fetchFaqs = async () => {
      try {
        const res = await axios.get("http://localhost:8000/faq/all");
        setFaqs(res.data.faqs);
      } catch (err) {
        setError("Failed to load FAQs.");
        console.error(err);
      }
    };

    fetchFaqs();
  }, []);

  return (
    <div className="max-w-4xl mx-auto px-6 py-10">
      <h2 className="text-3xl font-bold mb-6 text-blue-700">Frequently Asked Questions</h2>

      {error && <p className="text-red-600">{error}</p>}

      <div className="space-y-4">
        {faqs.map((faq, index) => (
          <div key={index} className="p-4 border rounded-lg shadow-md bg-white">
            <h3 className="font-semibold text-lg text-blue-600">{faq.question}</h3>
            <p className="text-gray-800 mt-2">{faq.answer}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default FAQViewer;
