// src/components/SymptomChecker.jsx
import React, { useState, useEffect, useRef } from "react";
import axios from "axios";
// import gsap from "gsap";

const SymptomChecker = () => {
  const [symptoms, setSymptoms] = useState("");
  const [result, setResult] = useState("");
  const [loading, setLoading] = useState(false);
  const containerRef = useRef(null);
  const resultRef = useRef(null);

//   useEffect(() => {
//     gsap.from(containerRef.current, {
//       opacity: 0,
//       y: 40,
//       duration: 1,
//       ease: "power4.out",
//     });
//   }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setResult("");

    try {
      const res = await axios.post("http://127.0.0.1:8000/predict/", {
        symptoms,
      });
      setResult(res.data.result);
    //   gsap.fromTo(
    //     resultRef.current,
    //     { opacity: 0, y: 20 },
    //     { opacity: 1, y: 0, duration: 0.6, ease: "power3.out" }
    //   );
    } catch (err) {
      setResult("An error occurred. Please try again.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <section
      ref={containerRef}
      className="min-h-screen flex items-center justify-center px-4 py-10 bg-gradient-to-br from-blue-100 to-white"
    >
      <div className="max-w-2xl w-full bg-white p-8 rounded-2xl shadow-2xl">
        <h2 className="text-3xl font-bold mb-6 text-blue-800">Symptom Checker</h2>
        <form onSubmit={handleSubmit} className="space-y-6">
          <textarea
            rows="5"
            className="w-full p-4 border border-gray-300 rounded-xl focus:outline-none focus:ring-2 focus:ring-blue-500 resize-none"
            placeholder="Enter your symptoms (e.g., headache fever fatigue)"
            value={symptoms}
            onChange={(e) => setSymptoms(e.target.value)}
            required
          ></textarea>
          <button
            type="submit"
            className="w-full py-3 bg-blue-600 text-white font-semibold rounded-xl shadow-md hover:bg-blue-700 transition duration-300"
            disabled={loading}
          >
            {loading ? "Checking..." : "Check Disease"}
          </button>
        </form>

        {result && (
          <div
            ref={resultRef}
            className="mt-8 p-4 bg-green-50 border border-green-400 text-green-800 rounded-lg shadow-md"
          >
            <strong>Predicted Disease:</strong> {result}
          </div>
        )}
      </div>
    </section>
  );
};

export default SymptomChecker;
